import React,{ useState, useEffect } from 'react';
import { getDatabase, ref, set as firebaseSet, onValue, push as firebasePush, remove } from 'firebase/database';

function AddListing(props) {

    // States
    const [isYes, setIsYes] = useState(false); 
    const [data, setData] = useState([]);
    const [currName, setCurrName] = useState("");
    const [currQuant, setCurrQuant] = useState("");
    const [currLocation, setCurrLocation] = useState("");
    const [currCategory, setCurrCat] = useState("");
    //const [currImage, setCurrImage] = useState(null);

    // ---------FIREBASE----------//
    useEffect(() => {
        const db = getDatabase();
        const foodRef = ref(db, "food");

        // Listen for changes in the data
        const unsubscribe = onValue(foodRef, function(snapshot) {
          const allDataObj = snapshot.val();

          // data w keys
          const keyArray = Object.keys(allDataObj);
          const allDataArr = keyArray.map((keyString) => {
            const dataObj = allDataObj[keyString];
            dataObj.firebasekey = keyString;
            console.log(keyString);
            return dataObj;
          });

          // update state with the new data
          setData(allDataArr);
        });

        // Cleanup the listener when the component unmounts
        return () => {
          unsubscribe();
        };
      }, []);

    // Write data into firebase
    function writeData(path, name, quant, location, category) { //imgURL 
        let dataPath = "";
        if(path == "claimed") {
            dataPath = "claimed/";
        }
        else {
            dataPath = "active/";
        }
        const db = getDatabase();
        firebasePush(ref(db, dataPath), {
            name: name, 
            quantity: quant, 
            location: location, 
            category: category
            //img: imgURL //-- ADD IN LATER
        })
        .then(() => console.log("data saved successfully!"))
        .catch(err => console.log(err)) }; 

    //  ---------EVENT HANDLERS------------ //

    // Questionnaire handler
    const yesHandleClick = (event) => {
        setIsYes(true);
      }

    // Update states with new added food
    // Name
    const nameHandleClick = (event) => {
      const name = event.target.value;
      setCurrName(name);
    }

    // Quantity
    const quantHandleClick = (event) => {
        const quant = event.target.value;
        setCurrQuant(quant);
      }

    // Location
    const locHandleClick = (event) => {
        const location = event.target.value;
        setCurrLocation(location);
    }

    // Category
    const catHandleClick = (event) => {
        const cat = event.target.value;
        setCurrCat(cat);
      }

    // Image
    // const imgHandleClick = (event) => {
    //     const img = event.target.value;
    //     setCurrImage(img);
    // }

    // --------ADD FIREBASE DATA---------- //
    const addData = () => {
        writeData("active", currName, currQuant, currLocation, currCategory)// currImage
        setCurrName("");
        setCurrQuant("");
        setCurrLocation("");
        setCurrCat("");
        // setCurrImage(null);
    }; 

    return (
        <div>
            <h2 className="survey-label"> Add New Food Listing </h2>

            {/* Create Form */}
            <form id="form">

                {/* Food eligiblity questionnaire */}
                <div class="form-control">
                    <label>
                        Before continuing, answer the following questions for eligibilty:
                        <ul>
                            <li> Is the food item unopened? </li>
                            <li> Is the food item's expiration date at least a week in the future from today? </li>
                            <li> If the food item is non-shelf stable or not a non-perishable, has the food item been properly stored the whole time it was in your possession (frozen or fridgerated foods)? </li>
                        </ul>
                        If no to any of these question, the food item is not eligible and cannot be listed.
                    </label>

                    <label for="survey-eligible">
                        <button class="btn btn-success filter-btn" id="survey-yes" onClick={yesHandleClick}>Yes</button>
                    </label>
                </div>
            </form>

            {/* Show form if questionnaire passes */}
            {isYes == true ? 
            (
                <div class="form-control">

                    {/* Food input */}
                    <div class="name-input"> 
                        <label for="name" id="label-name">
                            Food Name
                        </label>
                        <input type="text" placeholder="Chips" value={currName} onChange={nameHandleClick}/>
                    </div>

                    {/* Quantity input */}
                    <div class="quant-input"> 
                        <label for="quantity" id="label-quantity">
                            Item Quantity (1lb., 1oz)
                        </label>
                        <input type="text" placeholder="1 bag" value={currQuant} onChange={quantHandleClick} />
                    </div> 

                    {/* Location input */}
                    <div class="location-input"> 
                        <label for="quantity" id="label-quantity">
                            Zip Code
                        </label>
                        <input type="text" placeholder="98105" value={currLocation} onChange={locHandleClick} />
                    </div> 

                    {/* Category input */}
                    <div> 
                        <input type="radio" name="category" value="Meat or Dairy" id="meatDairy-category" onChange={catHandleClick} /> Meat or Dairy
                        <input type="radio" name="category" value="Beverage" id="beverage-category" onChange={catHandleClick} /> Beverage
                        <input type="radio" name="category" value="Produce" id="produce-category" onChange={catHandleClick} /> Produce 
                        <input type="radio" name="category" value="Non-perishable" id="nonperish-category" onChange={catHandleClick} /> Non-perishable (canned, boxed, dried fruit) 
                    </div> 

                    {/* Image  input -- NOT SURE IF THIS WORKS WILL TEST LATER */}
                    {/* <input type="file" id="photo" />
                    <button id="upload" onclick={imgHandleClick}>Upload Image</button> */}

                    {/* Submit button */}
                    <button class="btn btn-success filter-btn" id="new-item-submit" onClick={addData}>Submit</button>

                </div>
            ) 
            : 
            ( 
                <p> </p> // Return nothing if "Yes" is not checked
            )}

        </div>
    )
}

export {AddListing};
