import React,{ useState } from 'react';
import { getDatabase, ref, set as firebaseSet, onValue, push as firebasePush, remove } from 'firebase/database';

function AddListing(props) {

    // States
    const [isYes, setIsYes] = useState(false); 
    const [data, setData] = useState([]);
    const [currName, setCurrName] = useState("");
    const [currQuant, setCurrQuant] = useState("");
    const [currLocation, setCurrLocation] = useState("");
    const [currCategory, setCurrCat] = useState("");

    // Questionnaire handler
    const yesHandleClick = (event) => {
        setIsYes(true);
      }

    // Image handler 
    var loadFile = function(event) {
        var image = document.getElementById('output');
        image.src = URL.createObjectURL(event.target.files[0]);
    };

    return (
        <div>
            <h2 className="survey-label"> Add New Food Listing </h2>

            {/* Create Form */}
            <form id="form">

                {/* Food eligiblity questionnaire */}
                <div class="form-control">
                    <label>
                        Before continuing, answer the following questions for eligibilty:
                        <ul>
                            <li> Is the food item unopened? </li>
                            <li> Is the food item's expiration date at least a week in the future from today? </li>
                            <li> If the food item is non-shelf stable or not a non-perishable, has the food item been properly stored the whole time it was in your possession (frozen or fridgerated foods)? </li>
                        </ul>
                        If no to any of these question, the food item is not eligible and cannot be listed.
                    </label>

                    <label for="survey-eligible">
                        <button class="btn btn-success filter-btn" id="survey-yes" onClick={yesHandleClick}>Yes</button>
                    </label>
                </div>
            </form>

            {/* Show form if questionnaire passes */}
            {isYes == true ? 
            (
                <div class="form-control">

                    {/* Food input */}
                    <label for="name" id="label-name">
                        Food Name
                    </label>
                    <input type="text" id="quantity-input"
                        placeholder="Chips" />

                    {/* Quantity input */}
                    <label for="quantity" id="label-quantity">
                        Item Quantity (1lb., 1oz)
                    </label>
                    <input type="text" id="quantity-input"
                        placeholder="1 bag" />

                    {/* Location input */}
                    <label for="quantity" id="label-quantity">
                        Zip Code
                    </label>
                    <input type="text" id="zip-input"
                        placeholder="98105" />

                    {/* Category input */}
                    <input type="radio" name="category" value="Meat or Dairy" id="meatDairy-category"/> Meat or Dairy
                    <input type="radio" name="category" value="Beverage" id="beverage-category"/> Beverage
                    <input type="radio" name="category" value="Produce" id="produce-category"/> Produce 
                    <input type="radio" name="category" value="Non-perishable" id="nonperish-category"/> Non-perishable (canned, boxed, dried fruit) 

                    {/* Image  input -- NOT SURE IF THIS WORKS WILL TEST LATER */}
                    <label for="file">Upload Image</label>
                    <input type="file" accept="image/*" name="image" id="file" onchange= {() => loadFile()}/>
                    {/* <img id="output" width="200" /> */}

                </div>
            ) 
            : 
            ( 
                <p> </p> // Return nothing if "Yes" is not checked
            )}

        </div>
    )
}

export {AddListing};
